pragma solidity ^0.4.21;

import "zeppelin-solidity/contracts/math/SafeMath.sol";
import "zeppelin-solidity/contracts/ownership/Ownable.sol";
import "zeppelin-solidity/contracts/token/ERC20/ERC20.sol";

contract GamblingPlatform is Ownable {
    using SafeMath for uint256;

    uint256 public currentSupply;
    ERC20 nyanyukToken;

    uint256 public maxBet;
    address public tokenAddress;

    mapping(address => uint256) playerBalance;
    Bet[] betResult;

    struct Bet {
        bool isPlayerWon;
        uint diceResult;
        address playerAddress;
        uint256 amountOfBet;
    }

    event BetCreated(address indexed playerAddress, bool isPlayerWon, uint diceResult, uint256 amountOfBet);
    event PlayerDeposited(address indexed playerAddress, uint256 amount);
    event PlayerWithdrew(address indexed playerAddress, uint256 amount);
    event OwnerDeposited(uint256 amount);
    event OwnerWithdrew(uint256 amount);

    constructor(uint256 _maxBet, address _tokenAddress) public  {
        maxBet = _maxBet;
        tokenAddress = _tokenAddress;
        nyanyukToken = ERC20(tokenAddress);
    }

    modifier canBet(uint256 _betAmount){
        require(_betAmount > 0);
        require(_betAmount < maxBet);
        require(playerBalance[msg.sender] + _betAmount > 0);
        require(_betAmount <= playerBalance[msg.sender]);
        require(currentSupply >= _betAmount);
        _;
    }

    /**
     * @dev main function for the buyer to bet
     * @dev in case of owner cannot pay the player so current supply should more than the amount user can bet
     * @param _betAmount amount to bet
     */
    function bet(uint256 _betAmount) external canBet(_betAmount) returns (bool isPlayerWon) {
        uint8 diceResult = uint8(uint256(keccak256(block.blockhash(block.number - 1), block.timestamp, _betAmount, msg.sender, playerBalance[msg.sender])) % 101);
        isPlayerWon = diceResult > 50;
        Bet memory newBet = Bet(isPlayerWon, diceResult, msg.sender, _betAmount);
        betResult.push(newBet);
        emit BetCreated(msg.sender, isPlayerWon, diceResult, _betAmount);
        if (isPlayerWon == true) {
            currentSupply.sub(_betAmount);
            playerBalance[msg.sender].add(_betAmount);
        } else {
            currentSupply.add(_betAmount);
            playerBalance[msg.sender].sub(_betAmount);
        }
    }

    /**
     * @dev function for owner to deposit
     * @dev owner has to approve this contract before running this function
     * @dev if the amount he approve same than what he request, then contract will transfer from the owner address and add the current supply
     * @param _amount amount to deposit
     */
    function ownerDeposit(uint256 _amount) external onlyOwner() returns (bool) {
        require(_amount > 0);
        require(currentSupply + _amount > 0);
        if (nyanyukToken.transferFrom(owner, address(this), _amount) == true) {
            currentSupply = currentSupply.add(_amount);
            emit OwnerDeposited(_amount);
        }
        return true;
    }

    /**
     * @dev function for owner to withdraw the token based on currentSupply
     * @param _amount amount to deposit
     */
    function ownerWithdraw(uint256 _amount) external onlyOwner() returns (bool) {
        require(_amount > 0);
        require(currentSupply >= _amount);
        if (nyanyukToken.transfer(owner, _amount) == true) {
            currentSupply = currentSupply.sub(_amount);
            emit OwnerWithdrew(_amount);
        }
        return true;
    }

    /**
     * @dev function for the player to deposit token that he can use to do betting
     * @dev player has to approve the contract address with the certain amount he want to deposit before calling this function
     * @dev approving different amount to the contract than the _amount params supplied will yield an error
     * @param _amount amount to deposit
     */
    function playerDeposit(uint256 _amount) external returns (bool) {
        require(_amount > 0);
        require(playerBalance[msg.sender] + _amount > 0);
        require(nyanyukToken.allowance(msg.sender, address(this)) == _amount); //make sure we can get the money user deposit
        if (nyanyukToken.transferFrom(msg.sender, address(this), _amount) == true) {
            playerBalance[msg.sender] = playerBalance[msg.sender].add(_amount);
            emit PlayerDeposited(msg.sender, _amount);
        }
        return true;
    }

    /**
     * @dev function for the player to withdraw his token from the platform
     * @param _amount amount to withdraw
     */
    function playerWithdraw(uint256 _amount) external returns (bool) {
        require(_amount > 0);
        require(playerBalance[msg.sender] >= _amount);
        if (nyanyukToken.transfer(msg.sender, _amount) == true) {
            playerBalance[msg.sender] = playerBalance[msg.sender].sub(_amount);
            emit PlayerWithdrew(msg.sender, _amount);
        }
        return true;
    }

    /**
     * @dev function for owner to get current supply
     */
    function getCurrentSupply() external view returns (uint256) {
        return currentSupply;
    }

    /**
      * @dev function for player to get current balance
      */
    function getPlayerBalance() external view returns (uint256) {
        return playerBalance[msg.sender];
    }
}

